INSERT INTO ts_templates VALUES (1, 'HEADER', 'HTML placed at the top of every page', '<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN">\n<html>\n\n<head>\n<meta name="keywords" content="{TRIVIA_SITE_NAME}, {HOME_SITE_NAME}, quiz, quizzes, online quiz, test, exam, php, mysql, Pineapple Technologies">\n\n<meta name="description" content="{TRIVIA_SITE_NAME} - an interactive online quiz powered by QuizShock">\n\n<!-- DO NOT REMOVE OR MODIFY COPYRIGHT AND "POWERED BY" NOTICES. YOU MUST LEAVE THEM FULLY VISIBLE TO BE IN COMPLIANCE WITH THE LICENSE TERMS -->\n<title>{TRIVIA_SITE_NAME} :: powered by QuizShock</title>\n\n</head>\n\n<style type="text/css">\n\nbody {\n SCROLLBAR-HIGHLIGHT-COLOR: {BODY_BGCOLOR};\n SCROLLBAR-BASE-COLOR:  {TABLE_HEADER2_COLOR};\n SCROLLBAR-ARROW-COLOR: #FFFFFF;\n }\n\n .input {\n FONT-SIZE: 10px;\n FONT-FAMILY: Verdana,Arial,Helvetica,sans-serif;\n COLOR: #000000;\n BACKGROUND-COLOR: #FFFFFF;\n BORDER: 1 solid\n }\n	\n .button {\n FONT-SIZE: 10px;\n FONT-FAMILY: Verdana,Arial,Helvetica,sans-serif;\n FONT-WEIGHT: bold;\n COLOR: #FFFFFF;\n BACKGROUND-COLOR: {TABLE_HEADER2_COLOR};\n BORDER: 1 solid\n }\n\n a {\n COLOR: #C02C34;\n FONT-WEIGHT: BOLD;\n TEXT-DECORATION: NONE\n }\n\n a:hover {\n COLOR: {TABLE_HEADER2_COLOR}\n }\n\n .header1link {\n COLOR: #FFFFFF\n }\n\n .header1link:hover {\n COLOR: #BBC9DF\n }\n\n .header2link {\n COLOR: #FFFFFF;\n TEXT-DECORATION: NONE\n }\n\n .header2link:hover {\n COLOR: #FFFFFF;\n TEXT-DECORATION: UNDERLINE\n }\n\n .darklink {\n COLOR: #C02C34\n }\n\n .cell1link, .cell2link { \n COLOR: {TABLE_HEADER1_COLOR}\n}\n\n .darklink:hover, .cell1link:hover, .cell2link:hover {\n COLOR: {TABLE_HEADER2_COLOR}\n }\n\n.buttons {\nFONT-FAMILY: verdana,arial;\nFONT-SIZE: 10pt;\nFONT-WEIGHT: bold;\nBORDER: 1 dotted;\nBACKGROUND-COLOR: #FAFAFA\n}\n\nTH {\nFONT-FAMILY: verdana,arial;\nFONT-SIZE: 10pt;\nFONT-WEIGHT: bold;\nBACKGROUND-COLOR: {TABLE_HEADER1_COLOR}\n}\n\n.nav {\nFONT-FAMILY: verdana,arial;\nFONT-WEIGHT: BOLD;\nPADDING-BOTTOM: 10pt\n}\n\n.login_box {\nFONT-FAMILY: verdana,arial;\nFONT-WEIGHT: BOLD;\nBORDER: 1 solid;\nWIDTH: 75%;\nPADDING-TOP: 5pt;\nPADDING-BOTTOM: 5pt;\nPADDING-LEFT: 5pt;\nPADDING-RIGHT: 5pt\n}\n\n.table_header_1 {\nFONT-FAMILY: verdana,arial,\nBGCOLOR: #415E8D;\n}\n</style>\n\n<body bgcolor="{BODY_BGCOLOR}">\n\n<!-- YOU CAN INCLUDE YOUR OWN SITE HEADER HERE, USING THE PHP require() FUNCTION -->\n\n{NORMAL_FONT}\n\n<center>\n<table width="{MAIN_TABLE_WIDTH}" border=0 cellspacing=5 border=0 cellpadding=1>\n<tr><td bgcolor="{TABLE_BORDER_COLOR}" width="100%" align=center>\n\n<table width="100%" border=0 cellspacing=0 border=0 cellpadding=10>\n<tr><td bgcolor="{MAIN_TABLE_COLOR}" width="100%">\n\n<!-- A note to the administrator if the trivia site is offline -->\n{ADMIN_OFFLINE_NOTICE}\n\n<center>\n<table width="100%" cellspacing=10><tr><td align=center width="50%">\n<img src="images/quizshock.gif">\n</td>\n<td align=center width="50%">\n<font size=2>\n<div class=buttons>\n<b><a href="{TRIVIA_MAIN_PAGE_LINK}">HOME</a>\n\n::\n\n<b><a href="{REGISTER_LINK}">REGISTER</a>\n\n::\n\n<b><a href="{PROFILE_LINK}">PROFILE</a>\n\n::\n\n<b><a href="{FAQ_LINK}">FAQ</a>\n</div>\n\n</td></tr></table>\n</center>\n\n{NORMAL_FONT}\n\n', '<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN">\n<html>\n\n<head>\n<meta name="keywords" content="{TRIVIA_SITE_NAME}, {HOME_SITE_NAME}, quiz, quizzes, online quiz, test, exam, php, mysql, Pineapple Technologies">\n\n<meta name="description" content="{TRIVIA_SITE_NAME} - an interactive online quiz powered by QuizShock">\n\n<!-- DO NOT REMOVE OR MODIFY COPYRIGHT AND "POWERED BY" NOTICES. YOU MUST LEAVE THEM FULLY VISIBLE TO BE IN COMPLIANCE WITH THE LICENSE TERMS -->\n<title>{TRIVIA_SITE_NAME} :: powered by QuizShock</title>\n\n</head>\n\n<style type="text/css">\n\nbody {\n SCROLLBAR-HIGHLIGHT-COLOR: {BODY_BGCOLOR};\n SCROLLBAR-BASE-COLOR:  {TABLE_HEADER2_COLOR};\n SCROLLBAR-ARROW-COLOR: #FFFFFF;\n }\n\n .input {\n FONT-SIZ