INSERT INTO ts_templates VALUES (2, 'TRIVIA_MAIN_PAGE', 'The main page for your quiz site, lists the quizzes.', '<table cellspacing=0 cellpadding=0 border=0 width="100%">\n\n<font color="{TABLE_HEADER1_COLOR}">\n&nbsp;&nbsp;&nbsp; <b>:: Welcome to {TRIVIA_SITE_NAME}</b>\n</font>\n\n<br>\n\n<tr><td bgcolor="{TABLE_BORDER_COLOR}">\n<table cellpadding={TABLE_CELLPADDING} cellspacing={TABLE_BORDER_SIZE} border=0 width="100%">\n\n<tr><td bgcolor="#FAFAFA" width="50%" align=center>\n\n<table width="100%">\n\n<tr><td bgcolor="#FAFAFA" width="50%" align=left nowrap>\n{TABLE_CELL2_FONT}<small>\n<b>Registered Users:</b> {REGISTERED_USERS}<br>\n<b>Newest User:</b> {NEWEST_USER}\n</small>\n</td>\n\n<td bgcolor="#FAFAFA" width="50%" align=right nowrap>\n{TABLE_CELL2_FONT}<small>\n<b>Users Taking Quizzes:</b> {USERS_PLAYING}<br>\n<b>Total Quizzes Taken:</b> {GAMES_PLAYED}<br>\n</small>\n</td>\n</tr></table>\n\n<td bgcolor="{TABLE_CELL1_COLOR}" width="50%">\n{TABLE_CELL1_FONT}\n<small>\nWelcome! If you are a new user, you may want to check out the <a href="{FAQ_LINK}">FAQ</a>. You will need to <a href="{REGISTER_LINK}">register</a> before you can participate.\n</small>\n</font>\n</td></tr></table>\n</table>\n\n{NORMAL_FONT}\n\n<br>\n\n<table cellspacing=0 cellpadding=0 border=0 width=100%>\n<tr><td bgcolor={TABLE_BORDER_COLOR}>\n<table cellpadding={TABLE_CELLPADDING} cellspacing={TABLE_BORDER_SIZE} border=0 width="100%">\n\n<tr>\n<th align=center width="60%" nowrap colspan=2>\n{TABLE_HEADER1_FONT}\n<small><b>Quiz</b></small>\n</th>\n\n<th bgcolor="{TABLE_HEADER1_COLOR}" align=center nowrap>\n{TABLE_HEADER1_FONT}\n<small><b>Questions</b></small>\n</font>\n</th>\n\n<th bgcolor="{TABLE_HEADER1_COLOR}" align=center nowrap>\n{TABLE_HEADER1_FONT}\n<small><b>Times Taken</b></small>\n</font>\n</th>\n\n<th bgcolor="{TABLE_HEADER1_COLOR}" align=center nowrap>\n{TABLE_HEADER1_FONT}\n<small><b>High Score</b></small>\n</font>\n</th>\n</tr>\n\n{GAME_LIST}\n\n</table>\n</td></tr></table>\n', '<table cellspacing=0 cellpadding=0 border=0 width="100%">\n\n<font color="{TABLE_HEADER1_COLOR}">\n&nbsp;&nbsp;&nbsp; <b>:: Welcome to {TRIVIA_SITE_NAME}</b>\n</font>\n\n<br>\n\n<tr><td bgcolor="{TABLE_BORDER_COLOR}">\n<table cellpadding={TABLE_CELLPADDING} cellspacing={TABLE_BORDER_SIZE} border=0 width="100%">\n\n<tr><td bgcolor="#FAFAFA" width="50%" align=center>\n\n<table width="100%">\n\n<tr><td bgcolor="#FAFAFA" width="50%" align=left nowrap>\n{TABLE_CELL2_FONT}<small>\n<b>Registered Users:</b> {REGISTERED_USERS}<br>\n<b>Newest User:</b> {NEWEST_USER}\n</small>\n</td>\n\n<td bgcolor="#FAFAFA" width="50%" align=right nowrap>\n{TABLE_CELL2_FONT}<small>\n<b>Users Taking Quizzes:</b> {USERS_PLAYING}<br>\n<b>Total Quizzes Taken:</b> {GAMES_PLAYED}<br>\n</small>\n</td>\n</tr></table>\n\n<td bgcolor="{TABLE_CELL1_COLOR}" width="50%">\n{TABLE_CELL1_FONT}\n<small>\nWelcome! If you are a new user, you may want to check out the <a href="{FAQ_LINK}">FAQ</a>. You will need to <a href="{REGISTER_LINK}">register</a> before you can participate.\n</small>\n</font>\n</td></tr></table>\n</table>\n\n{NORMAL_FONT}\n\n<br>\n\n<table cellspacing=0 cellpadding=0 border=0 width=100%>\n<tr><td bgcolor={TABLE_BORDER_COLOR}>\n<table cellpadding={TABLE_CELLPADDING} cellspacing={TABLE_BORDER_SIZE} border=0 width="100%">\n\n<tr>\n<th align=center width="60%" nowrap colspan=2>\n{TABLE_HEADER1_FONT}\n<small><b>Quiz</b></small>\n</th>\n\n<th bgcolor="{TABLE_HEADER1_COLOR}" align=center nowrap>\n{TABLE_HEADER1_FONT}\n<small><b>Questions</b></small>\n</font>\n</th>\n\n<th bgcolor="{TABLE_HEADER1_COLOR}" align=center nowrap>\n{TABLE_HEADER1_FONT}\n<small><b>Times Taken</b></small>\n</font>\n</th>\n\n<th bgcolor="{TABLE_HEADER1_COLOR}" align=center nowrap>\n{TABLE_HEADER1_FONT}\n<small><b>High Score</b></small>\n</font>\n</th>\n</tr>\n\n{GAME_LIST}\n\n</table>\n</td></tr></table>\n', 0, 0);
INSERT INTO ts_templates VALUES (3, 'GAME_LISTING', 'Represents a single quiz listing on the main page. This is repeated for each quiz.', '<tr>\r\n\r\n<td bgcolor="{TABLE_CELL2_COLOR}" width="3%">\r\n&nbsp;\r\n</td>\r\n\r\n<td bgcolor="{TABLE_CELL1_COLOR}" width="60%">\r\n{TABLE_CELL1_FONT}\r\n<b><a href="{TRIVIA_GAME_INFO_LINK}" class=cell1link>{TRIVIA_GAME_NAME}</a></b><br>\r\n<small>{TRIVIA_GAME_DESCRIPTION}</small>\r\n</font>\r\n</td>\r\n\r\n<td bgcolor="{TABLE_CELL2_COLOR}" align=center>\r\n{TABLE_CELL2_FONT}\r\n{TRIVIA_GAME_NUM_QUESTIONS}\r\n</font>\r\n</td>\r\n\r\n<td bgcolor="{TABLE_CELL1_COLOR}" align=center>\r\n{TABLE_CELL1_FONT}\r\n{TRIVIA_GAME_NUM_PLAYS}\r\n</font>\r\n</td>\r\n\r\n<td bgcolor="{TABLE_CELL2_COLOR}" align=center>\r\n{TABLE_CELL2_FONT}\r\n<!-- Replaced with CURRENT_HIGH_SCORE template if there is a high score, or CURRENT_HIGH_SCORE_NONE if not -->\r\n{CURRENT_HIGH_SCORE}\r\n</td>\r\n</tr>', '<tr>\r\n\r\n<td bgcolor="{TABLE_CELL2_COLOR}" width="3%">\r\n&nbsp;\r\n</td>\r\n\r\n<td bgcolor="{TABLE_CELL1_COLOR}" width="60%">\r\n{TABLE_CELL1_FONT}\r\n<b><a href="{TRIVIA_GAME_INFO_LINK}" class=cell1link>{TRIVIA_GAME_NAME}</a></b><br>\r\n<small>{TRIVIA_GAME_DESCRIPTION}</small>\r\n</font>\r\n</td>\r\n\r\n<td bgcolor="{TABLE_CELL2_COLOR}" align=center>\r\n{TABLE_CELL2_FONT}\r\n{TRIVIA_GAME_NUM_QUESTIONS}\r\n</font>\r\n</td>\r\n\r\n<td bgcolor="{TABLE_CELL1_COLOR}" align=center>\r\n{TABLE_CELL1_FONT}\r\n{TRIVIA_GAME_NUM_PLAYS}\r\n</font>\r\n</td>\r\n\r\n<td bgcolor="{TABLE_CELL2_COLOR}" align=center>\r\n{TABLE_CELL2_FONT}\r\n<!-- Replaced with CURRENT_HIGH_SCORE template if there is a high score, or CURRENT_HIGH_SCORE_NONE if not -->\r\n{CURRENT_HIGH_SCORE}\r\n</td>\r\n</tr>', 2, 0);
