INSERT INTO ts_templates VALUES (29, 'COPPA_FORM', 'Parental permission form displayed for users under 13 years of age who want to register.', '{NORMAL_FONT}\n\nTo maintain compliance with COPPA (<a href="http://www.ftc.gov/opa/1999/9910/childfinal.htm" target="_blank">http://www.ftc.gov/opa/1999/9910/childfinal.htm</a>), our policy does not allow children under 13 years of age to sign up on this site without parental permission.\n\n<br><br>\n\nPlease read our <a href="{PRIVACY_LINK}" target="_blank">privacy policy</a> to learn how this site uses personal information.\n\n<br><br>\n\nThe form below should be printed and filled out by a parent or guardian\nof the person under 13 wishing to register on this site. The form can\nthen be faxed or mailed in:<br><br>\n\n<blockquote><blockquote>\n\n<b>Fax Number:</b> {COPPA_FAX_NUMBER}<br><br>\n\n<b>Address:</b><br>\n<pre>\n{COPPA_ADDRESS}\n</pre>\n\n</blockquote></blockquote>\n\n<br>\n<center>\n\n<table cellspacing=0 cellpadding=0 border=0 width="100%">\n<tr><td bgcolor={TABLE_BORDER_COLOR}>\n<table cellpadding={TABLE_CELLPADDING}  cellspacing=\n{TABLE_BORDER_SIZE} border=0 width=100%>\n\n<tr>\n<td bgcolor="{TABLE_CELL2_COLOR}" width="100%" align=center colspan=2>\n{TABLE_CELL2_FONT}\n<b>Required Information</b>\n</font>\n</td></tr>\n\n<tr>\n<td bgcolor="{TABLE_CELL1_COLOR}" width="25%">\n{TABLE_CELL1_FONT}\n<b>Username</b>\n</font>\n</td>\n\n<td bgcolor="{TABLE_CELL1_COLOR}" width="75%">\n&nbsp;\n</td>\n</tr>\n\n<tr>\n<td bgcolor="{TABLE_CELL1_COLOR}" width="25%">\n{TABLE_CELL1_FONT}\n<b>Password</b>\n</font>\n</td>\n\n<td bgcolor="{TABLE_CELL1_COLOR}" width="75%">\n&nbsp;\n</td>\n</tr>\n\n<tr>\n<td bgcolor="{TABLE_CELL1_COLOR}" width="25%">\n{TABLE_CELL1_FONT}\n<b>Email</b><br>\n<small>This will not be visible to other users</small>\n</font>\n</td>\n\n<td bgcolor="{TABLE_CELL1_COLOR}" width="75%">\n&nbsp;\n</td>\n</tr>\n\n\n<tr>\n<td bgcolor="{TABLE_CELL2_COLOR}" width="100%" align=center colspan=2>\n{TABLE_CELL2_FONT}\n<