DROP TABLE IF EXISTS ts_answers;
CREATE TABLE ts_answers (  id int(10) unsigned NOT NULL auto_increment,  answer varchar(100) default NULL,  answer_order tinyint(3) unsigned NOT NULL default '0',  question_id int(10) unsigned default NULL,  correct tinyint(3) unsigned default NULL,  PRIMARY KEY  (id),  UNIQUE KEY ID (id),  KEY question_id (question_id)) TYPE=MyISAM;

DROP TABLE IF EXISTS ts_blackboard;
CREATE TABLE ts_blackboard (  the_key varchar(50) default NULL,  the_value varchar(255) default NULL) TYPE=MyISAM;

DROP TABLE IF EXISTS ts_categories;
CREATE TABLE ts_categories (  id smallint(5) unsigned NOT NULL auto_increment,  name varchar(30) default NULL,  description varchar(100) default NULL,  PRIMARY KEY  (id),  UNIQUE KEY ID (id)) TYPE=MyISAM;

DROP TABLE IF EXISTS ts_game_cats;
CREATE TABLE ts_game_cats (  game_id smallint(5) unsigned default NULL,  category_id smallint(5) unsigned default NULL,  KEY game_id (game_id),  KEY category_id (category_id)) TYPE=MyISAM;

DROP TABLE IF EXISTS ts_game_sections;
CREATE TABLE ts_game_sections (  id int(10) unsigned NOT NULL auto_increment,  name varchar(50) default NULL,  description varchar(255) default NULL,  section_order tinyint(3) unsigned default '0',  PRIMARY KEY  (id),  UNIQUE KEY id (id)) TYPE=MyISAM;
UPDATE ts_game_sections SET id = 0;

DROP TABLE IF EXISTS ts_game_sessions;
CREATE TABLE ts_game_sessions (  id int(10) unsigned NOT NULL auto_increment,  game_id int(10) unsigned default NULL,  game_key char(32) NOT NULL default '0',  user_id int(10) unsigned default NULL,  question_number tinyint(4) unsigned NOT NULL default '0',  current_question int(11) unsigned NOT NULL default '0',  score int(11) default '0',  start_time int(11) unsigned NOT NULL default '0',  end_time int(11) unsigned NOT NULL default '0',  last_response_time int(11) unsigned NOT NULL default '0',  state tinyint(3) unsigned default '0',  num_questions_correct smallint(5) unsigned default '0',  num_questions_incorrect smallint(5) unsigned default '0',  num_questions_out_of_time smallint(5) unsigned default '0',  game_type tinyint(3) unsigned NOT NULL default '0',  end_type tinyint(3) unsigned NOT NULL default '0',  PRIMARY KEY  (id),  KEY game_id (game_id),  KEY user_id (user_id),  KEY game_type (game_type),  KEY end_type (end_type),  KEY score (score)) TYPE=MyISAM;

DROP TABLE IF EXISTS ts_games;
CREATE TABLE ts_games (  id smallint(5) unsigned NOT NULL auto_increment,  name varchar(30) default NULL,  description varchar(100) default NULL,  plays mediumint(10) unsigned NOT NULL default '0',  questions_per_session tinyint(3) unsigned NOT NULL default '0',  question_time_limit tinyint(3) unsigned default NULL,  correct_points smallint(5) unsigned NOT NULL default '0',  incorrect_penalty smallint(6) unsigned NOT NULL default '0',  num_high_scores tinyint(3) unsigned NOT NULL default '0',  high_score_period smallint(5) unsigned NOT NULL default '0',  show_correct_answer tinyint(3) unsigned NOT NULL default '0',  mode tinyint(3) unsigned NOT NULL default '0',  game_skin varchar(100) NOT NULL default '',  multicategory tinyint(3) unsigned NOT NULL default '0',  last_score_reset int(11) unsigned NOT NULL default '0',  game_order tinyint(3) unsigned NOT NULL default '0',  game_section_id int(10) unsigned NOT NULL default '0',  time_between_games mediumint(8) unsigned default '0',  PRIMARY KEY  (id),  UNIQUE KEY ID (id),  KEY mode (mode)) TYPE=MyISAM;

DROP TABLE IF EXISTS ts_option_groups;
CREATE TABLE ts_option_groups (  id tinyint(3) unsigned NOT NULL auto_increment,  name varchar(30) default NULL,  PRIMARY KEY  (id),  UNIQUE KEY ID (id)) TYPE=MyISAM;

DROP TABLE IF EXISTS ts_options;
CREATE TABLE ts_options (  name varchar(50) NOT NULL default '',  description varchar(255) NOT NULL default '0',  the_key varchar(50) default NULL,  the_value varchar(255) NOT NULL default '',  default_value varchar(255) NOT NULL default '',  type tinyint(3) unsigned NOT NULL default '0',  input varchar(15) NOT NULL default '',  required tinyint(1) NOT NULL default '0',  size tinyint(4) NOT NULL default '0',  group_id tinyint(3) unsigned NOT NULL default '0',  KEY the_key (the_key)) TYPE=MyISAM;


DROP TABLE IF EXISTS ts_questions;
CREATE TABLE ts_questions (  id int(10) unsigned NOT NULL auto_increment,  question varchar(125) default NULL,  answer_notes varchar(125) NOT NULL default '',  category_id smallint(5) unsigned default NULL,  PRIMARY KEY  (id),  KEY category_id (category_id),  KEY question (question)) TYPE=MyISAM;

DROP TABLE IF EXISTS ts_template_cache;
CREATE TABLE ts_template_cache (  the_key varchar(100) default NULL,  data mediumtext) TYPE=MyISAM;

DROP TABLE IF EXISTS ts_template_vars;
CREATE TABLE ts_template_vars (  id mediumint(8) unsigned NOT NULL auto_increment,  description varchar(100) default NULL,  the_key varchar(100) default NULL,  the_value varchar(255) default NULL,  default_value varchar(255) NOT NULL default '',  type tinyint(3) unsigned NOT NULL default '0',  PRIMARY KEY  (id),  UNIQUE KEY ID (id)) TYPE=MyISAM;


DROP TABLE IF EXISTS ts_templates;
CREATE TABLE ts_templates (  id smallint(3) unsigned NOT NULL auto_increment,  the_key varchar(30) default NULL,  description varchar(100) NOT NULL default '',  data mediumtext,  default_data text NOT NULL,  parent int(11) NOT NULL default '0',  changed tinyint(3) unsigned NOT NULL default '0',  PRIMARY KEY  (id),  UNIQUE KEY ID (id)) TYPE=MyISAM;

DROP TABLE IF EXISTS ts_users;
CREATE TABLE ts_users (  id int(10) unsigned NOT NULL auto_increment,  username varchar(20) default NULL,  password varchar(32) default NULL,  email varchar(100) default NULL,  level tinyint(3) unsigned NOT NULL default '0',  time_offset tinyint(4) NOT NULL default '0',  homepage varchar(100) NOT NULL default '',  location varchar(50) NOT NULL default '',  biography text NOT NULL,  icq_number varchar(25) NOT NULL default '',  aim_handle varchar(25) NOT NULL default '',  yahoo_handle varchar(25) NOT NULL default '',  activation_key mediumint(5) unsigned NOT NULL default '0',  date_registered int(10) unsigned NOT NULL default '0',  last_visit_time int(10) unsigned NOT NULL default '0',  games_played mediumint(8) unsigned NOT NULL default '0',  PRIMARY KEY  (id),  UNIQUE KEY ID (id),  KEY username (username),  KEY password (password),  KEY level (level),  KEY email (email)) TYPE=MyISAM;

