INSERT INTO ts_options (name, description, the_key, the_value, default_value, type, input, required, size, group_id) VALUES ( 'Trivia Online?', 'Whether or not trivia is accessible. You can use this option to turn the game off should you need to do maintenance.', 'TRIVIA_ONLINE', '1', '1', '1', '', '0', '0', '2');
INSERT INTO ts_options (name, description, the_key, the_value, default_value, type, input, required, size, group_id) VALUES ( 'Maximum Simultaneous Game Sessions', 'The maximum number of users that can simultaneously play trivia. Any additional users will be locked out. Leave blank (or set to 0) for unlimited users.', 'MAX_GAME_SESSIONS', '', '', '2', 'num', '0', '5', '2');
INSERT INTO ts_options (name, description, the_key, the_value, default_value, type, input, required, size, group_id) VALUES ( 'Interrupt Active Game Sessions?', 'Whether or not to interrupt any active game sessions if you set your trivia site to offline. If this is set to no, users who were currently playing games will be able to finish normally if you set the game offline, but no new game sessions can be started.', 'INTERRUPT_ACTIVE_GAME_SESSIONS', '0', '0', '1', '', '0', '0', '2');
INSERT INTO ts_options (name, description, the_key, the_value, default_value, type, input, required, size, group_id) VALUES ( 'Disallowed Usernames', 'Enter usernames (separated by a space) that users will not be able to choose as their username when they register. Note these are NOT case sensitive, so if you were to enter \"admin\", this would prevent sign ups under \"aDmIn\", \"ADMIN\", \"admin\", etc.', 'DISALLOWED_USERNAMES', 'admin', 'admin', '2', 'text', '0', '50', '3');
INSERT INTO ts_options (name, description, the_key, the_value, default_value, type, input, required, size, group_id) VALUES ( 'Disallowed Email Addresses', 'Enter email addresses (each separated by a space) that users will not be able to register under. Enter full email addresses (i.e. you@example.com).', 'DISALLOWED_EMAIL_ADDRESSES', 'user@example.com', 'user@example.com', '2', 'text', '0', '50', '3');
INSERT INTO ts_options (name, description, the_key, the_value, default_value, type, input, required, size, group_id) VALUES ( 'Enable GZIP page compression?', 'If set to yes, all HTML pages outputted by this software will be GZIP compressesed (which can cut the size 5 to 1 in some cases).  This feature is supported by nearly all modern browsers.<br><br>Note: This requires PHP 4.0.4 and ZLIB to use', 'ENABLE_GZIP_COMPRESSION', '0', '0', '1', '', '0', '0', '4');
INSERT INTO ts_options (name, description, the_key, the_value, default_value, type, input, required, size, group_id) VALUES ( 'Offline Message', 'The message to display to users when you set your trivia site to offline (by setting the previous option to \"No\")', 'OFFLINE_MESSAGE', 'Sorry, trivia is currently down for maintenance. Please check back later.', 'Sorry, trivia is currently down for maintenance. Please check back later.', '3', 'text', '1', '0', '2');
INSERT INTO ts_options (name, description, the_key, the_value, default_value, type, input, required, size, group_id) VALUES ( 'Use Email Activation System?', 'Whether or not to use email activation system when new users sign up. With this system, new sign ups are not valid until they click an activation link which is emailed to them at the address they use to sign up. Only applies if above option is set to Yes.', 'USE_EMAIL_ACTIVATION_SYSTEM', '1', '1', '1', '', '1', '0', '3');
