INSERT INTO ts_options (name, description, the_key, the_value, default_value, type, input, required, size, group_id) VALUES ( 'Date Format', 'The format of the date that is displayed throughout the program. <br><br>The format comes from the PHP date() function: <a href=\"http://www.php.net/manual/en/function.date.php\" target=\"_blank\">http://www.php.net/manual/en/function.date.php</a>', 'DATE_FORMAT', 'F j, Y', 'F j, Y', '2', 'text', '1', '15', '5');
INSERT INTO ts_options (name, description, the_key, the_value, default_value, type, input, required, size, group_id) VALUES ( 'Time Format', 'The format of the time that is displayed throughout the program. <br><br>To format comes from the PHP date() function: <a href=\"http://www.php.net/manual/en/function.date.php\" target=\"_blank\">http://www.php.net/manual/en/function.date.php</a>', 'TIME_FORMAT', 'g:ia', 'g:ia', '2', 'text', '1', '15', '5');
INSERT INTO ts_options (name, description, the_key, the_value, default_value, type, input, required, size, group_id) VALUES ( 'Contact Link', 'A link for your visitors to contact you. This can be a link to a contact form or a direct link to an email address (use: <b>mailto:you@example.com</b>).', 'CONTACT_LINK', 'mailto:you@example.com', 'mailto:you@example.com', '2', 'text', '1', '50', '1');
INSERT INTO ts_options (name, description, the_key, the_value, default_value, type, input, required, size, group_id) VALUES ( 'Send No-cache Headers?', 'Enabling this option will prevent your visitors\' browsers from caching pages. This will keep pages which update often fresh, but could use more bandwidth.', 'SEND_NOCACHE_HEADERS', '1', '1', '1', '', '0', '0', '4');
INSERT INTO ts_options (name, description, the_key, the_value, default_value, type, input, required, size, group_id) VALUES ( 'Email From', 'This is the address that will appear in the \"from\" field when emails are sent out from the software (such as the activation email mailed to a new user sign up).', 'EMAIL_FROM', 'noreply@example.com', 'noreply@example.com', '2', 'email', '0', '50', '3');
INSERT INTO ts_options (name, description, the_key, the_value, default_value, type, input, required, size, group_id) VALUES ( 'Use COPPA Registration System?', 'Whether or not to comply to COPPA (Detailed at: <a href=\"http://www.ftc.gov/opa/1999/9910/childfinal.htm\" target=\"_blank\">http://www.ftc.gov/opa/1999/9910/childfinal.htm</a>), which requires parental permission for children under 13 to register.', 'USE_COPPA', '1', '1', '1', '', '0', '0', '6');
