INSERT INTO ts_template_cache (the_key, data) VALUES ( 'GAME_INFO_PAGE', '<font size=2 face=\"verdana,arial\" color=\"#000000\">\n<b>\n:: <a href=\"{TRIVIA_MAIN_PAGE_LINK}\">Example.com Trivia</a> :: \n{TRIVIA_GAME_NAME}\n</b>\n</font>\n<br><br>\n\n<center>\n<table cellspacing=0 cellpadding=0 border=0 width=\"100%\">\n<tr><td bgcolor=#000000>\n<table cellpadding=4  cellspacing=\n1 border=0 width=100%>\n\n<tr>\n<td bgcolor=\"#184A9C\" width=\"100%\" align=center colspan=2>\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#FFFFFF\">\n<big><b>{TRIVIA_GAME_NAME}</b></big>\n</font>\n</td></tr>\n\n<tr>\n<td bgcolor=\"#FFFFFF\" width=\"100%\" align=center colspan=2>\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n{TRIVIA_GAME_DESCRIPTION}\n<br><br>\n\n<table width=\"100%\" cellspacing=0 cellpadding=3>\n<tr>\n<td width=\"50%\" align=center>\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<small>This game has been played <b>{TRIVIA_GAME_NUM_PLAYS}</b> times.</small>\n</td>\n<td width=\"50%\" align=center>\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<small>Current High Score: <b>{SCORE}</b> by <b>{USERNAME}</b></small>\n</td></tr></table>\n\n</td>\n</tr>\n\n\n\n\n</font>\n\n</td></tr>\n\n<tr>\n<td bgcolor=\"#4883E4\" width=\"100%\" align=center colspan=2>\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#FFFFFF\">\n<b>Game Rules</b>\n</font>\n\n</td></tr>\n\n<tr>\n<td bgcolor=\"#FFFFFF\" width=\"100%\" colspan=2>\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<blockquote>\n<ul type=square>\n<li>The game will consist of <b>{TRIVIA_GAME_QUESTIONS_PER_SESSION}</b> questions.\n<li>You will have <b>{TRIVIA_GAME_QUESTION_TIME_LIMIT}</b> seconds to answer each question.\n<li>You will score <b>{TRIVIA_GAME_CORRECT_POINTS}</b> points for answering a question correctly.\n<li>You will lose <b>{TRIVIA_GAME_INCORRECT_PENALTY}</b> points for answering a question\nincorrectly or running out of time.\n</ul>\n\n \n</blockquote>\n</font>\n\n</td></tr></table>\n</td></tr></table>\n\n<br>\n\n<table cellspacing=0 cellpadding=0 border=0 width=\"50%\">\n<tr><td bgcolor=\"#000000\">\n<table cellpadding=4 cellspacing=1 border=0 width=\"100%\">\n\n<tr><td bgcolor=\"#184A9C\" width=\"50%\" align=center nowrap>\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#FFFFFF\">\n<b><a href=\"javascript:void(null)\" onClick=\"javascript:window.open(\'{PLAY_LINK}\', \'play\', \'menubar=no, width=600, height=400, toolbar=no, scollbars=no, status=no, directories=no\')\" class=header1link>PLAY NOW!</a></b>\n</td>\n\n<td bgcolor=\"#184A9C\" width=\"50%\" align=center nowrap>\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#FFFFFF\">\n<b><a href=\"{HIGH_SCORE_LINK}\" class=header1link>VIEW HIGH SCORES</a></b>\n</font>\n</td></tr></table>\n</td></tr></table>\n\n<br>\n\n<table cellspacing=0 cellpadding=0 border=0 width=\"100%\">\n<tr><td bgcolor=#000000>\n<table cellpadding=4 cellspacing=\n1 border=0 width=100%>\n\n<td bgcolor=\"#4883E4\" width=\"100%\" align=center>\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#FFFFFF\">\n<big><b>Users currently playing: {NUM_PLAYERS}</b></big>\n</font>\n</td></tr>\n\n<tr>\n<td bgcolor=\"#FFFFFF\" width=\"100%\" align=center>\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n{USER_LIST}\n</font>\n\n</td></tr></table>\n</td></tr></table>\n\n\n<br>\n\n<br>');
