INSERT INTO ts_template_cache (the_key, data) VALUES ( 'LOGIN_FORM', '<table cellspacing=0 cellpadding=0 border=0>\n<tr><td bgcolor=#000000>\n<table cellpadding=4  cellspacing=1 border=0 width=100%>\n\n<td bgcolor=\"#FFFFFF\" align=center>\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<small>\n<form method=POST action=\"{SUBMIT_TO}\"><b>Username:</b> <input name=ts_username size=10 class=\"input\"> \n\n<!-- these are important, do not remove or edit them -->\n<input type=hidden name=\"forward_to\" value=\"{FORWARD_TO}\">\n<input type=hidden name=\"fn\" value=\"login\"> \n<b>Password:</b> <input name=ts_password size=10 type=password class=\"input\"> &nbsp;&nbsp;\n\n<input type=checkbox name=\"remember\" value=1 class=input> <small>Remember my login/password &nbsp;&nbsp;\n\n<input type=submit value=Login class=\"button\"> \n<br>\n<a href=\"{FORGOT_INFO_LINK}\" class=\"darklink\">Forgot your username/password?</a>\n</small>\n</font>\n</td>\n</tr>\n</table></table>');
