INSERT INTO ts_template_cache (the_key, data) VALUES ( 'ACTIVATION_EMAIL', 'Thank you for registering at Example.com Trivia. To activate your account so\nyou can start playing trivia games, point your web browser to the link below:\n\n{ACTIVATION_LINK}\n\n<a href=\"{ACTIVATION_LINK}\">Click here if you use AOL</a>\n\nMake sure to copy and paste the ENTIRE link into your browser (even if it\nhas been broken into two lines).\n\nYour user information is as follows:\n\nUsername: {USERNAME}\n\nPassword: {PASSWORD}\n\n\n--\nThank you,\n\nExample.com Trivia - Administrator\nhttp://10.0.0.3/trivia_1\n');
