INSERT INTO ts_template_cache (the_key, data) VALUES ( 'FOOTER', '<br><br>\n<center>{LOGIN_FORM}\n\n<br><br>\n\n<font size=2 face=\"verdana,arial\" color=\"#000000\">\n<small>The current time is: <b>{TIME}</b></small><br>\n\n<table cellspacing=0 cellpadding=0 border=0 width=\"75%\">\n<tr><td bgcolor=\"#000000\">\n<table cellpadding=4 cellspacing=1 border=0 width=\"100%\">\n\n<tr><td bgcolor=\"#FFFFFF\" width=\"100%\" align=center nowrap>\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<b><a href=\"http://www.example.com\" class=\"darklink\">Example.com</a> &nbsp; :: &nbsp; <a href=\"mailto:you@example.com\" class=\"darklink\">Contact Us</a>\n&nbsp;&nbsp;&nbsp;\n</font>\n</td></tr></table>\n</table>\n\n<!-- End main tables, started in HEADER template -->\n</td></tr></table></td></tr></table>\n\n<!-- Link to control panel if user is logged in as administrator -->\n{CONTROL_PANEL_LINK}\n\n<!-- DO NOT REMOVE OR MODIFY COPYRIGHT AND \"POWERED BY\" NOTICES. YOU MUST LEAVE THEM FULLY VISIBLE TO BE IN COMPLIANCE WITH THE LICENSE TERMS -->\n<br><center><small>\nPowered by TriviaShock {TS_VERSION}<br>\nCopyright (c)2001-2002 Pineapple Technologies. All Rights Reserved.\n<br><br>\n<!-- DO NOT REMOVE OR MODIFY COPYRIGHT AND \"POWERED BY\" NOTICES. YOU MUST LEAVE THEM FULLY VISIBLE TO BE IN COMPLIANCE WITH THE LICENSE TERMS -->\n\n<!-- Your copyright notice, set via the control panel -->\n\n\n</small></center>\n\n<br>\n\n\n<!-- YOU CAN INCLUDE YOUR OWN FOOTER HERE, USING THE PHP require() FUNCTION -->\n');
INSERT INTO ts_template_cache (the_key, data) VALUES ( 'COPPA_NOTICE', '<table cellspacing=0 cellpadding=0 border=0 width=\"100%\">\n<tr><td bgcolor=#000000>\n<table cellpadding=4  cellspacing=\n1 border=0 width=100%>\n\n<tr>\n<td bgcolor=\"#184A9C\" width=\"100%\" align=center colspan=2>\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#FFFFFF\">\n<b>Register at Example.com Trivia</b>\n</font>\n</td></tr>\n\n<tr>\n<td bgcolor=\"#FFFFFF\" width=\"100%\" align=center>\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\nBefore registering, please click the appropriate link below:<br><br>\n\n<a href=\"{OVER_13_LINK}\">I am 13 years old or over</a>\n\n<br><br>\n\n<a href=\"{UNDER_13_LINK}\">I am under 13 years old</a>\n\n<br><br>\n\nTo learn about how this site uses your information, please read our <a href=\"{PRIVACY_LINK}\" target=\"_blank\">privacy policy</a>\n\n</font>\n</td>\n\n</tr>\n\n</table>\n</td></tr></table>\n');
INSERT INTO ts_template_cache (the_key, data) VALUES ( 'COPPA_FORM', '<font size=2 face=\"verdana,arial\" color=\"#000000\">\n\nTo maintain compliance with COPPA (<a href=\"http://www.ftc.gov/opa/1999/9910/childfinal.htm\" target=\"_blank\">http://www.ftc.gov/opa/1999/9910/childfinal.htm</a>), our policy does not allow children under 13 years of age to sign up on this site without parental permission.\n\n<br><br>\n\nPlease read our <a href=\"{PRIVACY_LINK}\" target=\"_blank\">privacy policy</a> to learn how this site uses personal information.\n\n<br><br>\n\nThe form below should be printed and filled out by a parent or guardian\nof the person under 13 wishing to register on this site. The form can\nthen be faxed or mailed in:<br><br>\n\n<blockquote><blockquote>\n\n<b>Fax Number:</b> <br><br>\n\n<b>Address:</b><br>\n<pre>\n\n</pre>\n\n</blockquote></blockquote>\n\n<br>\n<center>\n\n<table cellspacing=0 cellpadding=0 border=0 width=\"100%\">\n<tr><td bgcolor=#000000>\n<table cellpadding=4  cellspacing=\n1 border=0 width=100%>\n\n<tr>\n<td bgcolor=\"#F0F0F0\" width=\"100%\" align=center colspan=2>\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<b>Required Information</b>\n</font>\n</td></tr>\n\n<tr>\n<td bgcolor=\"#FFFFFF\" width=\"25%\">\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<b>Username</b>\n</font>\n</td>\n\n<td bgcolor=\"#FFFFFF\" width=\"75%\">\n&nbsp;\n</td>\n</tr>\n\n<tr>\n<td bgcolor=\"#FFFFFF\" width=\"25%\">\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<b>Password</b>\n</font>\n</td>\n\n<td bgcolor=\"#FFFFFF\" width=\"75%\">\n&nbsp;\n</td>\n</tr>\n\n<tr>\n<td bgcolor=\"#FFFFFF\" width=\"25%\">\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<b>Email</b><br>\n<small>This will not be visible to other users</small>\n</font>\n</td>\n\n<td bgcolor=\"#FFFFFF\" width=\"75%\">\n&nbsp;\n</td>\n</tr>\n\n\n<tr>\n<td bgcolor=\"#F0F0F0\" width=\"100%\" align=center colspan=2>\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<b>Optional Information</b> - <small>Visible to other visitors on this site</small>\n</font>\n</td></tr>\n\n<tr>\n<td bgcolor=\"#FFFFFF\" width=\"25%\">\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<b>Homepage</b>\n</font>\n</td>\n\n<td bgcolor=\"#FFFFFF\" width=\"75%\">\n&nbsp;\n</td>\n</tr>\n\n<tr>\n<td bgcolor=\"#FFFFFF\" width=\"25%\">\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<b>Location</b>\n</font>\n</td>\n\n<td bgcolor=\"#FFFFFF\" width=\"75%\">\n&nbsp;\n</td>\n</tr>\n\n<tr>\n<td bgcolor=\"#FFFFFF\" width=\"25%\">\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<b>Biography</b>\n</font>\n</td>\n\n<td bgcolor=\"#FFFFFF\" width=\"75%\">\n<br><br><br><br><br><br>\n</td>\n</tr>\n\n<tr>\n<td bgcolor=\"#FFFFFF\" width=\"25%\">\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<b>ICQ Number</b>\n</font>\n</td>\n\n<td bgcolor=\"#FFFFFF\" width=\"75%\">\n&nbsp;\n</td>\n</tr>\n\n<tr>\n<td bgcolor=\"#FFFFFF\" width=\"25%\">\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<b>AOL Instant Messenger Handle</b>\n</font>\n</td>\n\n<td bgcolor=\"#FFFFFF\" width=\"75%\">\n&nbsp;\n</td>\n</tr>\n\n<tr>\n<td bgcolor=\"#FFFFFF\" width=\"25%\">\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<b>Yahoo! Messenger Handle</b>\n</font>\n</td>\n\n<td bgcolor=\"#FFFFFF\" width=\"75%\">\n&nbsp;\n</td>\n</tr>\n\n<tr>\n<td bgcolor=\"#FFFFFF\" width=\"25%\">\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<b>Yahoo! Messenger Handle</b>\n</font>\n</td>\n\n<td bgcolor=\"#FFFFFF\" width=\"75%\">\n&nbsp;\n</td>\n</tr>\n\n<tr>\n<td bgcolor=\"#FFFFFF\" width=\"25%\">\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<b>Time Offset</b><br>\n<small>\nThe offset of your time zone relative to GMT (i.e. -6, which is CST).\n</font>\n</td>\n\n<td bgcolor=\"#FFFFFF\" width=\"75%\">\n&nbsp;\n</td>\n</tr>\n\n<tr>\n<td bgcolor=\"#F0F0F0\" width=\"100%\" align=center colspan=2>\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<b>Parental Permission</b>\n</font>\n</td></tr>\n\n<tr>\n<td bgcolor=\"#FFFFFF\" width=\"25%\">\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<b>Parent or Guardian\'s Full Name</b>\n</font>\n</td>\n\n<td bgcolor=\"#FFFFFF\" width=\"75%\">\n&nbsp;\n</td>\n</tr>\n\n<tr>\n<td bgcolor=\"#FFFFFF\" width=\"25%\">\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<b>Parent or Guardian\'s Email Address</b>\n</font>\n</td>\n\n<td bgcolor=\"#FFFFFF\" width=\"75%\">\n&nbsp;\n</td>\n</tr>\n\n<tr>\n<td bgcolor=\"#FFFFFF\" width=\"25%\">\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<b>Parent or Guardian\'s Phone Number</b>\n</font>\n</td>\n\n<td bgcolor=\"#FFFFFF\" width=\"75%\">\n&nbsp;\n</td>\n</tr>\n\n\n<tr>\n<td bgcolor=\"#FFFFFF\" width=\"25%\">\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<b>Parent/Guardian\'s Signature</b><br>\n<small>Authorizing your child to register on this web site.</small>\n</font>\n</td>\n\n<td bgcolor=\"#FFFFFF\" width=\"75%\">\nX_____________________________________\n</td>\n</tr>\n\n\n<tr>\n<td bgcolor=\"#FFFFFF\" width=\"25%\">\n<font size=2 face=\"verdana,arial,helvetica\" color=\"#000000\">\n<b>Date</b>\n</font>\n</td>\n\n<td bgcolor=\"#FFFFFF\" width=\"75%\">\n&nbsp;\n</td>\n</tr>\n\n\n</table></td></tr></table>\n');
INSERT INTO ts_template_cache (the_key, data) VALUES ( 'PRIVACY_POLICY', '<font size=2 face=\"verdana,arial\" color=\"#000000\">\n\n<b>\n:: <a href=\"http://10.0.0.3/trivia_1\">Example.com Trivia</a> :: Privacy Policy</b>\n<br>\n\n<blockquote>\n\nWe respect your privacy. The information which is collected via the registration form is used only for internal purposes and is not disclosed to third parties.\n\n<br><br>\n\n<b>Protection of Information</b>\n\n<br><br>\n\nYour email address and password will never be visible to other users of this site. Only your username and the optional fields will be visible to other users. Your email address and other information is not disclosed to bulk email lists or any other third party.\n\n<br><br>\n\n<b>Usage Information</b>\n\n<br><br>\n\nStatistics on your usage and your information are used only for internal and logging purposes and are not disclosed to third parties. Any statistics or logs of usage that are disclosed only represent the combined, generalized statistics of many users and in no way reveal any information that represents you personally.\n\n<br><br>\n\n<b>Right to Modify your Information</b>\n\n<br><br>\n\nYou will be allowed to modify all of your registration information (excluding your username and password) at any time by clicking the \"Profile\" link and logging in.\n\n<br><br>\n\n<b>Use of Cookies</b>\n\n<br><br>\n\nCookies are used to store your username and password on your local computer when you log in. This will <i>not</i> make your username and password available to any other users. It is used only so that you can remain logged into the site and be automatically logged in on repeat visits (if you specify).\n\n</blockquote>\n\n');
