INSERT INTO ts_templates (id, the_key, description, data, default_data, parent, changed) VALUES ( '19', 'LOGIN_FORM', 'Form which allows users to login to the site.', '<table cellspacing=0 cellpadding=0 border=0>\n<tr><td bgcolor={TABLE_BORDER_COLOR}>\n<table cellpadding={TABLE_CELLPADDING}  cellspacing={TABLE_BORDER_SIZE} border=0 width=100%>\n\n<td bgcolor=\"{TABLE_CELL1_COLOR}\" align=center>\n{TABLE_CELL1_FONT}\n<small>\n<form method=POST action=\"{SUBMIT_TO}\"><b>Username:</b> <input name=ts_username size=10 class=\"input\"> \n\n<!-- these are important, do not remove or edit them -->\n<input type=hidden name=\"forward_to\" value=\"{FORWARD_TO}\">\n<input type=hidden name=\"fn\" value=\"login\"> \n<b>Password:</b> <input name=ts_password size=10 type=password class=\"input\"> &nbsp;&nbsp;\n\n<input type=checkbox name=\"remember\" value=1 class=input> <small>Remember my login/password &nbsp;&nbsp;\n\n<input type=submit value=Login class=\"button\"> \n<br>\n<a href=\"{FORGOT_INFO_LINK}\" class=\"darklink\">Forgot your username/password?</a>\n</small>\n</font>\n</td>\n</tr>\n</table></table>', '<table cellspacing=0 cellpadding=0 border=0>\n<tr><td bgcolor={TABLE_BORDER_COLOR}>\n<table cellpadding={TABLE_CELLPADDING}  cellspacing={TABLE_BORDER_SIZE} border=0 width=100%>\n\n<td bgcolor=\"{TABLE_CELL1_COLOR}\" align=center>\n{TABLE_CELL1_FONT}\n<small>\n<form method=POST action=\"{SUBMIT_TO}\"><b>Username:</b> <input name=ts_username size=10 class=\"input\"> \n\n<!-- these are important, do not remove or edit them -->\n<input type=hidden name=\"forward_to\" value=\"{FORWARD_TO}\">\n<input type=hidden name=\"fn\" value=\"login\"> \n<b>Password:</b> <input name=ts_password size=10 type=password class=\"input\"> &nbsp;&nbsp;\n\n<input type=checkbox name=\"remember\" value=1 class=input> <small>Remember my login/password &nbsp;&nbsp;\n\n<input type=submit value=Login class=\"button\"> \n<br>\n<a href=\"{FORGOT_INFO_LINK}\" class=\"darklink\">Forgot your username/password?</a>\n</small>\n</font>\n</td>\n</tr>\n</table></table>', '0', '0');
INSERT INTO ts_templates (id, the_key, description, data, default_data, parent, changed) VALUES ( '20', 'LOGOUT_FORM', 'Shown when a user is logged in. Gives them the option (link) to log out.', 'You are currently logged in as: <b>{USERNAME}</b>. &nbsp; <a href=\"{LOGOUT_LINK}\" class=\"darklink\">Logout</a>', 'You are currently logged in as: <b>{USERNAME}</b>. &nbsp; <a href=\"{LOGOUT_LINK}\" class=\"darklink\">Logout</a>', '0', '0');
INSERT INTO ts_templates (id, the_key, description, data, default_data, parent, changed) VALUES ( '21', 'MESSAGE', 'A message displayed to the user (thanks for signing up, error, bad login, logging you in, etc.).', '<br><br><center>\n<table cellspacing=0 cellpadding=0 border=0 width=\"75%\">\n<tr><td bgcolor={TABLE_BORDER_COLOR}>\n<table cellpadding={TABLE_CELLPADDING}  cellspacing=\n{TABLE_BORDER_SIZE} border=0 width=100%>\n<td bgcolor=\"{TABLE_HEADER1_COLOR}\" width=\"100%\">\n{TABLE_HEADER1_FONT}\n<b>TriviaShock Message</b>\n</font>\n</td></tr>\n\n<tr>\n<td bgcolor=\"{TABLE_CELL1_COLOR}\" width=\"100%\">\n{TABLE_CELL1_FONT}\n{MESSAGE}\n</font>\n</td></tr></table>\n</td></tr></table>\n</center>\n<br><br>', '<br><br><center>\n<table cellspacing=0 cellpadding=0 border=0 width=\"75%\">\n<tr><td bgcolor={TABLE_BORDER_COLOR}>\n<table cellpadding={TABLE_CELLPADDING}  cellspacing=\n{TABLE_BORDER_SIZE} border=0 width=100%>\n<td bgcolor=\"{TABLE_HEADER1_COLOR}\" width=\"100%\">\n{TABLE_HEADER1_FONT}\n<b>TriviaShock Message</b>\n</font>\n</td></tr>\n\n<tr>\n<td bgcolor=\"{TABLE_CELL1_COLOR}\" width=\"100%\">\n{TABLE_CELL1_FONT}\n{MESSAGE}\n</font>\n</td></tr></table>\n</td></tr></table>\n</center>\n<br><br>', '0', '0');
INSERT INTO ts_templates (id, the_key, description, data, default_data, parent, changed) VALUES ( '22', 'ACTIVATION_EMAIL', 'Email mailed to new users. Contains account activation link.', 'Thank you for registering at {TRIVIA_SITE_NAME}. To activate your account so\nyou can start playing trivia games, point your web browser to the link below:\n\n{ACTIVATION_LINK}\n\n<a href=\"{ACTIVATION_LINK}\">Click here if you use AOL</a>\n\nMake sure to copy and paste the ENTIRE link into your browser (even if it\nhas been broken into two lines).\n\nYour user information is as follows:\n\nUsername: {USERNAME}\n\nPassword: {PASSWORD}\n\n\n--\nThank you,\n\n{TRIVIA_SITE_NAME} - Administrator\n{TRIVIA_SITE_URL}\n', 'Thank you for registering at {TRIVIA_SITE_NAME}. To activate your account so\nyou can start playing trivia games, point your web browser to the link below:\n\n{ACTIVATION_LINK}\n\n<a href=\"{ACTIVATION_LINK}\">Click here if you use AOL</a>\n\nMake sure to copy and paste the ENTIRE link into your browser (even if it\nhas been broken into two lines).\n\nYour user information is as follows:\n\nUsername: {USERNAME}\n\nPassword: {PASSWORD}\n\n\n--\nThank you,\n\n{TRIVIA_SITE_NAME} - Administrator\n{TRIVIA_SITE_URL}\n', '0', '0');
INSERT INTO ts_templates (id, the_key, description, data, default_data, parent, changed) VALUES ( '23', 'ACTIVATION_EMAIL_SUBJECT', 'The email subject line of the activation email sent to new user sign ups. Must be only text.', '{TRIVIA_SITE_NAME} - Activating your Account', '{TRIVIA_SITE_NAME} - Activating your Account', '22', '0');
INSERT INTO ts_templates (id, the_key, description, data, default_data, parent, changed) VALUES ( '24', 'FORGOT_INFO_FORM', 'Form given to user to allow them to retrive their forgotten/lost username and password.', '<br><br><center>\n<table cellspacing=0 cellpadding=0 border=0 width=\"75%\">\n<tr><td bgcolor={TABLE_BORDER_COLOR}>\n<table cellpadding={TABLE_CELLPADDING}  cellspacing=\n{TABLE_BORDER_SIZE} border=0 width=100%>\n<td bgcolor=\"{TABLE_HEADER1_COLOR}\" width=\"100%\">\n{TABLE_HEADER1_FONT}\n<b>Retrieve lost username/password</b>\n</font>\n</td></tr>\n\n<tr>\n<td bgcolor=\"{TABLE_CELL1_COLOR}\" width=\"100%\" align=center>\n{TABLE_CELL1_FONT}\n\nUse this form to retrieve your lost username and have your password reset. Enter the email address which you used when you registered. Your username and a temporary password will be emailed to that address.\n\n<br><br>\n\n<form method=POST action=\"{SUBMIT_TO}\">\n<b>Email Address:</b> <input name=email value=\"\" class=input> <input type=submit name=submit value=\"Submit\" class=button>\n</form>\n\n</font>\n</td></tr></table>\n</td></tr></table>\n</center>\n<br><br>\n\n', '<br><br><center>\n<table cellspacing=0 cellpadding=0 border=0 width=\"75%\">\n<tr><td bgcolor={TABLE_BORDER_COLOR}>\n<table cellpadding={TABLE_CELLPADDING}  cellspacing=\n{TABLE_BORDER_SIZE} border=0 width=100%>\n<td bgcolor=\"{TABLE_HEADER1_COLOR}\" width=\"100%\">\n{TABLE_HEADER1_FONT}\n<b>Retrieve lost username/password</b>\n</font>\n</td></tr>\n\n<tr>\n<td bgcolor=\"{TABLE_CELL1_COLOR}\" width=\"100%\" align=center>\n{TABLE_CELL1_FONT}\n\nUse this form to retrieve your lost username and have your password reset. Enter the email address which you used when you registered. Your username and a temporary password will be emailed to that address.\n\n<br><br>\n\n<form method=POST action=\"{SUBMIT_TO}\">\n<b>Email Address:</b> <input name=email value=\"\" class=input> <input type=submit name=submit value=\"Submit\" class=button>\n</form>\n\n</font>\n</td></tr></table>\n</td></tr></table>\n</center>\n<br><br>\n\n', '0', '0');
INSERT INTO ts_templates (id, the_key, description, data, default_data, parent, changed) VALUES ( '25', 'FORGOT_INFO_EMAIL', 'Email sent to a user which contains their username and password.', 'Your username and temporary password for {TRIVIA_SITE_NAME} is as follows:\n\nUsername: {USERNAME}\n\nPassword: {PASSWORD}\n\n\nYou can change your password by editing your profile.\n\n--\nThank you,\n{TRIVIA_SITE_NAME} - Administrators\n{TRIVIA_SITE_URL}', 'Your username and temporary password for {TRIVIA_SITE_NAME} is as follows:\n\nUsername: {USERNAME}\n\nPassword: {PASSWORD}\n\n\nYou can change your password by editing your profile.\n\n--\nThank you,\n{TRIVIA_SITE_NAME} - Administrators\n{TRIVIA_SITE_URL}', '0', '0');
INSERT INTO ts_templates (id, the_key, description, data, default_data, parent, changed) VALUES ( '26', 'FORGOT_INFO_EMAIL_SUBJECT', 'Subject line on the email sent when a user forgets their login information. Must be text only.', '{TRIVIA_SITE_NAME} - User Information', '{TRIVIA_SITE_NAME} - User Information', '25', '0');
