$SQL[] = "CREATE TABLE config ( config_id tinyint(1) NOT NULL auto_increment, siteURL varchar(100) NOT NULL default '', menuType tinyint(2) NOT NULL default '0', webmasterEmail varchar(100) NOT NULL default '', numberOfNews tinyint(3) NOT NULL default '0', headlines tinyint(1) NOT NULL default '0', dateFormat varchar(16) NOT NULL default '', lang varchar(30) NOT NULL default '', siteName varchar(40) NOT NULL default '', barColor varchar(16) NOT NULL default '', theme_id tinyint(3) NOT NULL default '1', version varchar(12) NOT NULL default '', footer text NOT NULL, keywords text,  description text,  forums tinyint(1) NOT NULL default '0',  close_site tinyint(1) NOT NULL default '0',  PRIMARY KEY  (config_id)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE forums_avatars (  av_id int(5) NOT NULL auto_increment,  user_id int(10) NOT NULL default '0',  name varchar(50) NOT NULL default '',  file varchar(200) NOT NULL default '',  PRIMARY KEY  (av_id),  KEY user_id (user_id)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE forums_cat (  cat_id int(5) NOT NULL auto_increment,  name varchar(100) NOT NULL default '',  ord tinyint(2) NOT NULL default '0',  PRIMARY KEY  (cat_id),  KEY ord (ord)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE forums_config (  account_activate tinyint(1) NOT NULL default '0',  topics_per_page int(4) NOT NULL default '0',  posts_per_page int(4) NOT NULL default '0',  popular_threshold int(4) NOT NULL default '0',  max_poll tinyint(1) NOT NULL default '0',  allow_html tinyint(1) NOT NULL default '0',  allow_xcode tinyint(1) NOT NULL default '0',  allow_smiles tinyint(1) NOT NULL default '0',  allow_sig tinyint(1) NOT NULL default '0',  max_sig_length int(10) NOT NULL default '0',  allow_avatars tinyint(1) NOT NULL default '0',  remote_avatars tinyint(1) NOT NULL default '0',  avatar_upload tinyint(1) NOT NULL default '0',  allow_imood tinyint(1) NOT NULL default '0',  censor_words tinyint(1) NOT NULL default '0',  allow_ranks tinyint(1) NOT NULL default '0',  auto_prune tinyint(1) NOT NULL default '0',  prune_number int(5) NOT NULL default '0',  polls tinyint(1) NOT NULL default '0',  edit_post tinyint(1) NOT NULL default '0',  agreement text,  cellWidth int(4) NOT NULL default '500') TYPE=MyISAM;";
$SQL[] = "CREATE TABLE forums_forums (  forum_id int(11) NOT NULL auto_increment,  cat_id int(5) NOT NULL default '0',  name varchar(25) NOT NULL default '',  fdescription text,  level tinyint(1) NOT NULL default '0',  posts int(10) NOT NULL default '0',  topics int(10) NOT NULL default '0',  post_id int(10) NOT NULL default '0',  ord tinyint(2) NOT NULL default '0',  PRIMARY KEY  (forum_id),  KEY cat_id (cat_id)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE forums_mod (  forum_mod_id int(10) NOT NULL auto_increment,  user_id int(10) NOT NULL default '0',  forum_id int(10) NOT NULL default '0',  PRIMARY KEY  (forum_mod_id),  KEY user_id (user_id),  KEY forum_id (forum_id)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE forums_notify (  forum_notify_id int(10) NOT NULL auto_increment,  user_id int(10) NOT NULL default '0',  forum_id int(10) NOT NULL default '0',  topic_id int(10) NOT NULL default '0',  PRIMARY KEY  (forum_notify_id),  KEY forum_user (user_id,forum_id),  KEY topic_user (topic_id,user_id)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE forums_poll_results (  forum_results_id int(10) NOT NULL auto_increment,  forum_poll_id int(10) NOT NULL default '0',  option_id tinyint(2) NOT NULL default '0',  option_text text NOT NULL,  option_results int(10) NOT NULL default '0',  PRIMARY KEY  (forum_results_id),  KEY forum_poll_id (forum_poll_id)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE forums_polls (  forum_poll_id int(10) NOT NULL auto_increment,  topic_id int(10) NOT NULL default '0',  forum_poll_text text NOT NULL,  total int(10) NOT NULL default '0',  PRIMARY KEY  (forum_poll_id),  UNIQUE KEY id (forum_poll_id),  KEY id_2 (forum_poll_id),  KEY topic_id (topic_id)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE forums_polls_users (  forum_poll_user_id int(10) NOT NULL auto_increment,  forum_poll_id int(10) NOT NULL default '0',  user_id int(10) NOT NULL default '0',  PRIMARY KEY  (forum_poll_user_id),  KEY forum_poll_id (forum_poll_id),  KEY user_id (user_id)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE forums_posts (  post_id int(5) NOT NULL auto_increment,  topic_id int(5) NOT NULL default '0',  forum_id int(5) NOT NULL default '0',  user_id int(5) NOT NULL default '0',  title varchar(255) NOT NULL default '',  post text NOT NULL,  date int(16) NOT NULL default '0',  ip_addr varchar(20) NOT NULL default '',  PRIMARY KEY  (post_id),  KEY ff (topic_id,forum_id,user_id),  FULLTEXT KEY post (post),  FULLTEXT KEY title (title)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE forums_ranks (  rank_id int(5) NOT NULL auto_increment,  rank_name varchar(50) NOT NULL default '',  rank_min int(5) NOT NULL default '0',  rank_image varchar(50) NOT NULL default '',  rank_spec tinyint(1) NOT NULL default '0',  PRIMARY KEY  (rank_id),  UNIQUE KEY rank_id (rank_id),  KEY rank_min (rank_min)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE forums_search (  search_id int(10) NOT NULL default '0',  forum_id int(10) NOT NULL default '0',  topic_id int(10) NOT NULL default '0',  title varchar(255) NOT NULL default '',  user_id int(10) NOT NULL default '0',  posts int(10) NOT NULL default '0',  reads int(10) NOT NULL default '0',  last_post_id int(10) NOT NULL default '0',  last_post_date int(16) NOT NULL default '0',  last_post_user_id int(10) NOT NULL default '0',  KEY search (search_id),  KEY forum_id (forum_id),  KEY topic_id (topic_id)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE forums_search_control (  search_id int(10) NOT NULL auto_increment,  date int(16) NOT NULL default '0',  rows int(10) NOT NULL default '0',  PRIMARY KEY  (search_id)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE forums_session (  sess_id int(5) NOT NULL auto_increment,  user_id int(10) NOT NULL default '0',  last int(16) NOT NULL default '0',  templast int(16) NOT NULL default '0',  temp int(16) NOT NULL default '0',  PRIMARY KEY  (sess_id)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE forums_smiles (  smile_id int(10) NOT NULL auto_increment,  code varchar(10) NOT NULL default '',  image varchar(100) NOT NULL default '',  description varchar(50) NOT NULL default '',  PRIMARY KEY  (smile_id),  UNIQUE KEY code (code)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE forums_topics (  topic_id int(5) NOT NULL auto_increment,  forum_id int(5) NOT NULL default '0',  post_id int(5) NOT NULL default '0',  forum_poll_id int(10) NOT NULL default '0',  reads int(5) NOT NULL default '0',  sticky tinyint(1) NOT NULL default '0',  locked tinyint(1) NOT NULL default '0',  date int(16) NOT NULL default '0',  posts int(10) NOT NULL default '0',  last_post_id int(10) NOT NULL default '0',  PRIMARY KEY  (topic_id),  UNIQUE KEY topic_id (topic_id),  KEY forum_id (forum_id),  KEY sticky (sticky),  KEY some (sticky,forum_id)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE forums_words (  word_id int(5) NOT NULL auto_increment,  word varchar(25) NOT NULL default '',  replacement varchar(25) NOT NULL default '',  PRIMARY KEY  (word_id),  UNIQUE KEY word_id (word_id),  FULLTEXT KEY word (word)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE forums_xcode (  xcode_id int(10) NOT NULL auto_increment,  xcode_find varchar(255) NOT NULL default '',  xcode_replace varchar(255) NOT NULL default '',  PRIMARY KEY  (xcode_id)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE help (  help_id int(10) NOT NULL auto_increment,  sub int(10) NOT NULL default '0',  title varchar(250) NOT NULL default '',  text text NOT NULL,  ord int(10) NOT NULL default '0',  PRIMARY KEY  (help_id),  KEY sub (sub),KEY ord (ord),  FULLTEXT KEY text (text)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE images (  image_id mediumint(3) NOT NULL auto_increment,  title varchar(255) NOT NULL default '',  file varchar(255) NOT NULL default '',  size int(12) NOT NULL default '0',  PRIMARY KEY  (image_id),  KEY image_id (image_id)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE menu (  menu_id mediumint(3) NOT NULL auto_increment,  menu_name varchar(50) NOT NULL default '',  link varchar(255) NOT NULL default '',  target tinyint(1) NOT NULL default '0',  sub mediumint(3) NOT NULL default '0',  ord mediumint(3) NOT NULL default '0',  PRIMARY KEY  (menu_id),  KEY ord (ord),  KEY sub (sub),  KEY menu_id (menu_id)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE news (  news_id mediumint(3) NOT NULL auto_increment,  title varchar(150) NOT NULL default '',  post text NOT NULL,  date int(16) NOT NULL default '0',  PRIMARY KEY  (news_id),  KEY news_id (news_id),  FULLTEXT KEY post (post)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE pages (  page_id tinyint(2) NOT NULL auto_increment,  title varchar(150) NOT NULL default '',  body text NOT NULL,  page_index tinyint(1) NOT NULL default '0',  PRIMARY KEY  (page_id),  KEY page_id (page_id),  KEY page_index (page_index),  FULLTEXT KEY body (body)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE stats (  stats_id int(10) NOT NULL auto_increment,  type tinyint(1) NOT NULL default '0',  name varchar(255) NOT NULL default '',  count int(10) NOT NULL default '0',  PRIMARY KEY  (stats_id),  KEY name (name),  KEY type (type)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE theme (  theme_id int(10) NOT NULL auto_increment,  theme_name varchar(50) NOT NULL default '',  theme_author varchar(65) default NULL,  theme_description text,  theme_menu tinyint(2) default NULL,  theme_dir varchar(100) NOT NULL default '',  PRIMARY KEY  (theme_id)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE users (  user_id int(10) NOT NULL auto_increment,  username varchar(16) NOT NULL default '',  first_name varchar(30) NOT NULL default '',  last_name varchar(30) NOT NULL default '',  email varchar(60) NOT NULL default '',  password varchar(32) NOT NULL default '',  suspend tinyint(1) NOT NULL default '0',  admin tinyint(1) NOT NULL default '0',  regdate int(16) NOT NULL default '0',  view_email tinyint(1) NOT NULL default '0',  sig text,  attach_sig tinyint(1) NOT NULL default '0',  html tinyint(1) NOT NULL default '0',  xcode tinyint(1) NOT NULL default '0',  rank int(10) NOT NULL default '0',  posts int(10) NOT NULL default '0',  imood text,  av_id int(10) NOT NULL default '0',  remote_avatar varchar(250) default NULL,  website text,  loc varchar(250) default NULL,  occ varchar(250) default NULL,  interest text,  aim varchar(100) default NULL,  yim varchar(100) default NULL,  msn varchar(100) default NULL,  icq varchar(50) default NULL,  view_online tinyint(1) NOT NULL default '0',  actkey varchar(64) default NULL,  PRIMARY KEY  (user_id),  KEY admin (admin),  KEY suspend (suspend),  KEY login (username,password),  FULLTEXT KEY userSearch (username,first_name,last_name,email),  FULLTEXT KEY username (username)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE users_ban (  user_ban_id int(10) NOT NULL auto_increment,  user_id int(10) default NULL,  ip_addr varchar(20) default NULL,  PRIMARY KEY  (user_ban_id),  KEY user_id (user_id),  KEY ip_addr (ip_addr)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE forums_ignore (  user_id int(10) NOT NULL default '0',  ignore_user_id int(10) NOT NULL default '0',  KEY user_id (user_id)) TYPE=MyISAM;";
$SQL[] = "CREATE TABLE forums_pm (  pm_id int(10) NOT NULL auto_increment,  user_id int(10) NOT NULL default '0',  from_user_id int(10) NOT NULL default '0',  subject text NOT NULL,  body text NOT NULL,  date int(16) NOT NULL default '0',  deleted tinyint(1) NOT NULL default '0',  view tinyint(1) NOT NULL default '0',  PRIMARY KEY  (pm_id),  KEY user_id (user_id),  KEY date (date),  KEY deleted (deleted),  KEY view (view)) TYPE=MyISAM;";




