INSERT INTO %PREFIX%mail_templates (`mt_id`, `mt_name`, `mt_text`) VALUES (13, 'email_custom_order_request', 'Hello $_MTP[to_name],\r\nThe following email from $_MTP[from_name] was sent through the $_MTP[site] Custom Order Request system, and is a copy for your records.\r\n\r\nWe will be contacting you with the results of our processing the information you requested.\r\n\r\nThe following client profile was created as a result of your order request. In the event you choose to place the order, you can login to place the order.\r\n\r\n-------------------\r\n$_MTP[cl_info]\r\n-------------------\r\n\r\nThe following is the information you entered into the Custom Order Request form.\r\n\r\n-------------------\r\n$_MTP[cor_info]\r\n-------------------\r\n\r\n\r\nThank you for continuing to choose $_MTP[site] for your web service needs.\r\n\r\n $_MTP[from_name]\r\n $_MTP[from_email]\r\n');
