CREATE TABLE %PREFIX%downloads (`dload_id` int(11) NOT NULL auto_increment, `dload_group` varchar(50) NOT NULL default '', `dload_name` varchar(50) NOT NULL default '', `dload_desc` text NOT NULL, `dload_count` int(11) NOT NULL default '0', `dload_date_str` varchar(10) NOT NULL default 'yyyy-mm-dd', `dload_avail` int(1) NOT NULL default '0', `dload_filename` varchar(50) NOT NULL default '', `dload_filesize` varchar(8) NOT NULL default '', `dload_contributor` varchar(255) NOT NULL default '', PRIMARY KEY  (`dload_id`)) TYPE=MyISAM COMMENT='Downloads Table';
UPDATE %PREFIX%versions SET v_ts=%TIMESTAMP%, v_ver='v1.2.2', v_type='Upgrade';